import sql from 'mssql';
import dbConfig from "../config/connectDB";

let getHomepage = async (req, res) => {
    let data;
    const capDV = 3;
    await sql.connect(dbConfig).then(function (err) {

        var sqlrequest = new sql.Request();
        let result = sqlrequest.query(`exec DonViTheoCap @capDV = ${capDV}`).then(function (recordset) {
            sql.close(function (value) {
                console.log("connection closed");
            });
            // console.log(recordset);
            data = recordset.recordset;
            console.log(data);
            console.log(data.length);
            return res.render('index.ejs', { dataDV: data })

        }).catch(function (err) {
            console.log(err);
        });
    }).catch(function (err) {
        console.log(err);
    });

}

let getDetailTrungDoan = async (req, res) => {
    // let data;
    // const dvCapTren = req.param.id_DV;
    // await sql.connect(dbConfig).then(function (err) {

    //     var sqlrequest = new sql.Request();
    //     let result = sqlrequest.query(`exec DonViTrucThuoc @dvCapTren = ${dvCapTren}`).then(function (recordset) {
    //         sql.close(function (value) {
    //             console.log("connection closed");
    //         });
    //         // console.log(recordset);
    //         data = recordset.recordset;
    //         console.log(data);
    //         console.log(data.length);
    //         return res.render('detailDonVi.ejs', { dataDV: data })

    //     }).catch(function (err) {
    //         console.log(err);
    //     });
    // }).catch(function (err) {
    //     console.log(err);
    // });

    return res.render('detailDonVi.ejs')
}

module.exports = {
    getHomepage, getDetailTrungDoan
}

